var moment = require('moment');
var parseExpression = require('cron-parser').parseExpression;
var ALIASES = {
	'dayOfMonth': 'date',
	'dayOfWeek': 'day'
}

module.exports = function( cronTime ){
	var now = moment();
	var { _fields: interval } = parseExpression( cronTime );
	for( var key in interval ){
		var _key = ALIASES[ key ] || key;
		if(interval[ key ].indexOf( now[ _key ]() ) < 0){
			return false;
		}
	}
	return true;
}