var assert = require('assert');
var sleep = require('sleep-promise');

module.exports = function( init, by ){
	assert(typeof init === 'number', 'First argument must be a number.')
	assert(typeof by == 'function' || typeof by == 'number', 'Second argument must be either a function or a number.');
	var _init = init;
	var _by = 'number' === typeof by && function(n){ return n += by } || by;
	return function*(){
		_init = _by( _init )
		return yield sleep( _init );
	}
}